<?php

function malika_name_email_filter($email){
$email = explode('@',$email);
$name = str_replace  ("'", "", $email[0]);
$name = preg_replace ('/[^\p{L}\p{N}]/u', '_', $name);

var_dump ($name);

return $name;
}

function malika_message_html_coupon($code){
	$file = "template/coupon_code.php";
	
	if ( !file_exists( $file ) ) {
		return 'file not found';
	}
	
	ob_start();
		include $file;
	return ob_get_clean();

}

function malika_send_mail_coupon($email,$code){
	$to['name'] = malika_name_email_filter($email);
	$to['mail'] = $email;
	$msg = malika_message_html_coupon($code);

	malika_phpmailer_popup($to,$msg);
}

function malika_phpmailer_popup($to,$msg){
	include "mailer/class.phpmailer.php";
	
	$mail = new PHPMailer; 
	$mail->IsSMTP();
	//$mail->SMTPSecure = 'tls'; 
	$mail->Host = "malika.id"; //host masing2 provider email
	$mail->SMTPDebug = 0;
	$mail->Port = 25;
	$mail->SMTPAuth = true;
	$mail->Username = "admin@malika.id"; //user email
	$mail->Password = "kecapbangau2017"; //password email 
	$mail->SetFrom('admin@malika.id','Admin Malika'); //set email pengirim
	$mail->AddAddress($to['mail'],$to['name']);  //tujuan email
	$mail->IsHTML(true);
	$mail->Subject = 'Kode Kupon - new Customer '; //subyek email
	$mail->CharSet="utf-8";
	$mail->MsgHTML($msg);
	
	if($mail->Send()){
		$data = [
			'class'  => 'mcp_check',
			'node'   => '<img src="data:image/png;base64,"',
			'msg'    => 'congratulations !',
			'status' => 'cek email kamu sekarang juga'
		];
		print_r(json_encode($data));
	}else{
		$data = [
			'class'  => 'mcp_ups',
			'node'   => '<img src="data:image/png;base64,">',
			'msg'    => 'uuppss !',
			'status' => '@Error Server : maaf email anda tidak terkirim'
		];
		print_r(json_encode($data));
	}
}